INSERT INTO `movies` (`id`, `thumb`, `name`, `eng_name`, `url_page`, `url_movie`, `run_time`, `rate`, `country`, `content`, `createdAt`, `updatedAt`) VALUES
(1, 'https://phimmoiyyy.net/wp-content/uploads/2023/04/Bua-Hinh-Nhan.jpg', 'Bùa Hình Nhân', 'Hoon Payon 2023 ', 'https://phimmoiyyy.net/phim-le/bua-hinh-nhan', 'https://helvid.com/play/index/3474c188d9cb', '111 phút', '6.3', 'Thailand', 'Bùa Hình Nhân - được lấy cảm hứng từ loại bùa hình nhân hộ mệnh nổi tiếng ở Thái Lan, phim theo chân Tham trong hành trình đến một hòn đảo hẻo lánh để tìm người anh trai của mình đang tu hành ở đó. Tham phát hiện ra anh trai đã bị sát hại sau khi bị buộc tội giết người và trộm cắp. Quyết tâm ở lại hòn đảo để điều tra cũng như minh oan cho anh trai nhưng Tham lại vô tình khám phá ra nhiều cái chết bí ẩn khác tại ngôi làng bên cạnh.', '2024-01-12 10:07:09', '2024-01-12 10:07:09'),
(2, 'https://phimmoiyyy.net/wp-content/uploads/2022/07/hon-ma-khong-dau.jpg', 'Hồn Ma Không Đầu', 'Ivanna 2022 ', 'https://phimmoiyyy.net/phim-le/hon-ma-khong-dau', 'https://hd1080.opstream2.com/share/0359f3653b20ee2edfa3cc67ecefc35a', '103 phút', '6.3', 'Indonesia', 'Hồn Ma Không Đầu - nói về Ambar cô gái sở hữu đôi mắt âm dương cùng người em Dika đến ở tại một viện dưỡng lão. Tại đây, họ đã phát hiện ra sự thật kinh hoàng về hồn ma không đầu Ivanna và một phần lịch sử thảm khốc của nước nhà.', '2024-01-12 10:07:09', '2024-01-12 10:07:09'),
(3, 'https://phimmoiyyy.net/wp-content/uploads/2023/04/Phim-Ma-Lai-Rut-Ruot.jpg', 'Ma Lai Rút Ruột', 'Inhuman Kiss 2 2023 ', 'https://phimmoiyyy.net/phim-le/ma-lai-rut-ruot', 'https://1080.opstream4.com/share/e71fc4c35a684450f5eff49d2046f880', '123 phút', '6.1', 'Thailand', 'Ma Lai Rút Ruột - truyền thuyết Ma Lai rút ruột vang danh Đông Nam Á một lần nữa ứng nghiệm. Khi Ma Lai rung động với chàng trai bị nguyền rủa, mối tình lãng mạn nhưng cũng vô cùng rùng rợn liệu có lối thoát? Cloud (Jayler Krissanapoom) sinh ra với chứng rối loạn di truyền dẫn tới bệnh bạch tạng. Sau nhiều năm sống cô độc vì bị xa lánh, Cloud gặp được một cô gái có tên là Sao (Nink Chanya McClory). Cloud nhanh chóng nảy sinh tình cảm với Sao mà không hề biết rằng, cô gái này mang trong mình một bí mật vô cùng khủng khiếp.', '2024-01-12 10:07:10', '2024-01-12 10:07:10'),
(4, 'https://phimmoiyyy.net/wp-content/uploads/2022/11/ma-so-truc-quy.jpg', 'Ma Sơ Trục Quỷ', 'La Exorcista 2023 ', 'https://phimmoiyyy.net/phim-le/ma-so-truc-quy', 'https://www.youtube.com/embed/bQOBWUrgu6s ', '101 phút', '6.9', 'Mexico', 'Ma Sơ Trục Quỷ - Ofelia là một nữ tu trẻ vừa đặt chân đến thị trấn San Ramon đã bị ép phải thực hiện buổi lễ trừ tà cho một phụ nữ đang mang thai. Tưởng chừng buổi trục quỷ đã hoàn tất, Ofelia bàng hoàng nhận ra hiện thân quỷ dữ chưa từng biến mất.', '2024-01-12 10:07:10', '2024-01-12 10:07:10'),
(5, 'https://phimmoiyyy.net/wp-content/uploads/2023/11/Nam-Dem-Kinh-Hoang.jpg', 'Năm Đêm Kinh Hoàng', 'Five Nights at Freddy\'s 2023 ', 'https://phimmoiyyy.net/phim-le/nam-dem-kinh-hoang', 'https://1080.opstream4.com/share/2fb58b6192dd1ccdaa7574c54e5f78be', '110 phút', '8.6', 'USA', 'Năm Đêm Kinh Hoàng - nhân viên bảo vệ Mike bắt đầu làm việc tại Freddy Fazbear’s Pizza. Trong đêm làm việc đầu tiên, anh nhận ra mình sẽ không dễ gì vượt qua được ca đêm ở đây. Chẳng mấy chốc, anh sẽ vén màn sự thật đã xảy ra tại Freddy’s.', '2024-01-12 10:07:10', '2024-01-12 10:07:10'),
(6, 'https://phimmoiyyy.net/wp-content/uploads/2023/10/Yeu-Lai-Vo-Ngau.jpg', 'Yêu Lại Vợ Ngầu', 'Love Reset 2023 ', 'https://phimmoiyyy.net/phim-le/yeu-lai-vo-ngau', 'https://vie2.opstream7.com/share/0731460a8a5ce1626210cbf4385ae0ef', '119 phút', '8.5', 'South Korea', 'Yêu Lại Vợ Ngầu - kể về cặp vợ chồng trẻ No Jung Yeol (Kang Ha-neul) và Hong Na Ra (Jung So-min) từ cuộc sống hôn nhân màu hồng dần “hiện nguyên hình” trở thành cái gai trong mắt đối phương với vô vàn thói hư, tật xấu. Không thể đi đến tiếng nói chung, Jung Yeol và Na Ra quyết định ra toà ly dị.', '2024-01-12 10:07:10', '2024-01-12 10:07:10'),
(7, 'https://phimmoiyyy.net/wp-content/uploads/2023/11/Godzilla-Minus-One.jpg', 'Godzilla Minus One', 'Godzilla Minus One 2023 ', 'https://phimmoiyyy.net/phim-le/godzilla-minus-one', 'https://www.youtube.com/embed/VvSrHIX5a-0?si=N7C6kfyI8mti71Ia ', '125 phút', '8.8', 'Japan', 'Godzilla Minus One - lấy bối cảnh sau chiến tranh thế giới lần thứ 2, Nhật Bản khi này chịu thiệt hại nặng nề. Dẫu vậy, vẫn còn một mối đe dọa khác đến từ tự nhiên, một loài quái vật có sức hủy diệt khổng lồ trỗi dậy trên chính đống đổ nát đó', '2024-01-12 10:07:10', '2024-01-12 10:07:10'),
(8, 'https://phimmoiyyy.net/wp-content/uploads/2024/04/Godzilla-x-Kong-De-Che-Moi.jpg', 'Godzilla x Kong: Đế Chế Mới', 'Godzilla x Kong: The New Empire 2024 ', 'https://phimmoiyyy.net/phim-le/godzilla-x-kong-de-che-moi', 'https://www.youtube.com/embed/RXc2bs_aBuA?si=5HE7AT3qFXTCIM0Q ', '150 phút', '8.3', 'USA', 'Godzilla x Kong: Đế Chế Mới - sẽ tiếp nối sau những cuộc đối đầu bùng nổ của “Godzilla đại chiến Kong”, là một cuộc phiêu lưu điện ảnh hoàn toàn mới, đưa Kong toàn năng và Godzilla đáng sợ cùng nhau chống lại một mối đe dọa khổng lồ chưa được khám phá, ẩn sâu trong thế giới của chúng ta, thách thức sự tồn tại của chính chúng – và cả chúng ta.', '2024-01-12 10:07:11', '2024-01-12 10:07:11'),
(9, 'https://phimmoiyyy.net/wp-content/uploads/2023/09/Conan-Movie-26.jpg', 'Conan Movie 26: Tàu Ngầm Sắt Màu Đen', 'Detective Conan Movie 26 ', 'https://phimmoiyyy.net/phim-le/conan-movie-26-tau-ngam-sat-mau-den', 'https://1080.opstream4.com/share/64a2ced1a3bc35f45f1c3bdb0c8b256f', '110 phút', '9', 'Japan', 'Conan Movie 26: Tàu Ngầm Sắt Màu Đen - địa điểm lần này được đặt ở vùng biển gần đảo Hachijo-jima, Tokyo. Các kỹ sư từ khắp nơi trên thế giới đã tập hợp để vận hành toàn diện “Phao Thái Bình Dương”, một cơ sở ngoài khơi để kết nối các camera an ninh thuộc sở hữu của lực lượng cảnh sát trên toàn thế giới.', '2024-01-12 10:07:11', '2024-01-12 10:07:11'),
(10, 'https://phimmoiyyy.net/wp-content/uploads/2023/09/Ke-Kien-Tao.jpg', 'Kẻ Kiến Tạo', 'The Creator 2023 ', 'https://phimmoiyyy.net/phim-le/ke-kien-tao', 'https://1080.opstream4.com/share/8118a0ab4c07a91814ac5df70cbbaad5', '134 phút', '7.1', 'Canada', 'Kẻ Kiến Tạo - lấy bối cảnh cuộc chiến trong tương lai giữa loài người và trí tuệ nhân tạo. Joshua, một cựu đặc vụ đang đau buồn trước sự mất tích của người vợ, được thuê để tiêu diệt Creator, kẻ đã đứng sau phát triển một loại vũ khí có sức mạnh chấm dứt mọi cuộc chiến và sự sống của toàn thể nhân loại. Joshua và nhóm đặc vụ tinh nhuệ đã lần theo dấu vết tiến vào lãnh thổ bị A.I chiếm đóng, chỉ để khám phá ra vũ khí hủy diệt thế giới mà anh ta được giao tiêu diệt là một A.I dưới hình hài của một đứa trẻ.', '2024-01-12 10:07:11', '2024-01-12 10:07:11'),
(11, 'https://phimmoiyyy.net/wp-content/uploads/2023/09/Thanh-Guom-Tru-Ta.jpg', 'Thanh Gươm Trừ Tà', 'Dr. Cheon and Lost Talisman 2023 ', 'https://phimmoiyyy.net/phim-le/thanh-guom-tru-ta', 'https://hdbo.opstream5.com/share/9407be1bd4c5f4d01dd9b1a971290160', '98 phút', '8.8', 'South Korea', 'Thanh Gươm Trừ Tà - xoay quanh nhân vật thầy trừ tà Cheon (Gang Dong Won thủ vai), cùng với chiến hữu của mình In Bae (Lee Dong Hwi thủ vai), chuyên đi lừa đảo với những màn trừ tà “pha ke” sử dụng công nghệ cao cùng khả năng hùng biện và tài “thao túng tâm lý” đỉnh cao. Tuy chuyên đi trừ tà nhưng cả hai đều không thể nhìn thấy và không tin vào ma quỷ cho tới khi họ phải đối đầu với một con ác quỷ thật đang khống chế em gái (Park So Yi thủ vai) của Yoo Kyung (Esom thủ vai).', '2024-01-12 10:07:11', '2024-01-12 10:07:11'),
(12, 'https://phimmoiyyy.net/wp-content/uploads/2023/09/PAW-Patrol-Phim-Sieu-Dang.jpg', 'PAW Patrol: Phim Siêu Đẳng', 'PAW Patrol: The Mighty Movie 2023 ', 'https://phimmoiyyy.net/phim-le/paw-patrol-phim-sieu-dang', 'https://hd1080.opstream2.com/share/f9f0cd50df8a94019fc0391c61b96f3b', '92 phút', '8.9', 'Canada', 'PAW Patrol: Phim Siêu Đẳng - một mảnh thiên thạch kỳ diệu đã rơi xuống Thành Phố Phiêu Lưu, mang đến siêu năng lực cho những chú cún PAW Patrol và biến chúng thành Những Chú Cún Quyền Năng.', '2024-01-12 10:07:12', '2024-01-12 10:07:12'),
(13, 'https://phimmoiyyy.net/wp-content/uploads/2023/08/Nhan-Duyen-Tien-Dinh.jpg', 'Nhân Duyên Tiền Đình', 'Honey Sweet 2023 ', 'https://phimmoiyyy.net/phim-le/nhan-duyen-tien-dinh', 'https://hdbo.opstream5.com/share/1227f1221621bd026563d53b9cc7d864', '118 phút', '8.7', 'South Korea', 'Nhân Duyên Tiền Đình - chuyện phim xoay quanh nhân vật Chi-ho (Yoo Hae-jin) – nhà nghiên cứu bim bim với khả năng nếm vị xuất chúng, nhưng lại ngờ nghệch với mọi thứ xung quanh. Chi-ho là một người cực kỳ hướng nội, thích ở một mình và sống như một cái máy được lập trình sẵn mà không hề mắc lỗi dù chỉ một giây.', '2024-01-12 10:07:12', '2024-01-12 10:07:12'),
(14, 'https://phimmoiyyy.net/wp-content/uploads/2023/09/Biet-doi-danh-thue-4.jpg', 'Biệt Đội Đánh Thuê 4', 'The Expendables 4 2023 ', 'https://phimmoiyyy.net/phim-le/biet-doi-danh-thue-4-711416', 'https://1080.opstream4.com/share/f427229e017816d795d7dad1c4b6ed21', '103 phút', '8.2', 'USA', 'Biệt Đội Đánh Thuê 4 -là phim hành động Mỹ được đạo diễn bởi Scott Waugh với phần kịch bản do Spenser Cohen, Max Adams và John Joseph Connolly chấp bút. Đây là phần hậu truyện của Biệt đội đánh thuê 3 và đồng thời cũng là phần phim thứ bốn của loạt phim Biệt đội đánh thuê.', '2024-01-12 10:07:12', '2024-01-12 10:07:12'),
(15, 'https://phimmoiyyy.net/wp-content/uploads/2023/08/Phim-Blue-Beetle.jpg', 'Blue Beetle', 'Blue Beetle 2023 ', 'https://phimmoiyyy.net/phim-le/blue-beetle-185426', 'https://hd1080.opstream2.com/share/7b6ad2297d3beb569ddf3ee1ce22ffa8', '110 phút', '5.9', 'USA', 'Blue Beetle - một phiên bản cải tiến của nhân vật này, nhà khảo cổ học Dan Garrett, được giới thiệu vào năm 1964 bởi Charlton Comics, đã vẽ ra những khả năng thần bí từ một con bọ hung Ai Cập cổ đại. Được xuất bản bởi Charlton Comics và sau đó là DC, tác phẩm sáng tạo năm 1966 Ted Kord là học trò của Garret, người tiếp tục di sản chiến đấu chống tội phạm lâu đời của mình, mặc dù anh ta không có siêu năng lực.', '2024-01-12 10:07:13', '2024-01-12 10:07:13'),
(16, 'https://phimmoiyyy.net/wp-content/uploads/2023/09/Thien-ac-doi-dau-3.jpg', 'Thiện Ác Đối Đầu 3', 'The Equalizer 3 2023 ', 'https://phimmoiyyy.net/phim-le/thien-ac-doi-dau-3-661114', 'https://1080.opstream4.com/share/3d5caa905203cb23e50296184c6ca735', '109 phút', '9.5', 'Italy', 'Thiện Ác Đối Đầu 3 - nói về Robert McCall chuyển đến sinh sống tại miền Nam nước Ý nhưng phát hiện ra bạn bè của mình bị một tổ chức mafia ở địa phương kiểm soát. Khi tính mạng của họ bị đe dọa, ông buộc phải quay trở lại con đường sát thủ để bảo vệ bạn bè.', '2024-01-12 10:07:13', '2024-01-12 10:07:13'),
(17, 'https://phimmoiyyy.net/wp-content/uploads/2023/10/quy-am-tin-do.jpg', 'Quỷ Ám: Tín Đồ', 'The Exorcist: Believer 2023 ', 'https://phimmoiyyy.net/phim-le/quy-am-tin-do-154212', 'https://kd.opstream3.com/share/33b2db40961e2919636d06f5db60a964', '111 phút', '6.9', 'USA', 'Quỷ Ám: Tín Đồ - phần tiếp theo của bộ phim năm 1973 kể về một cô bé 12 tuổi bị một thực thể ma quỷ bí ẩn chiếm hữu, buộc mẹ cô phải tìm đến sự giúp đỡ của hai linh mục để cứu cô.', '2024-01-12 10:07:13', '2024-01-12 10:07:13'),
(18, 'https://phimmoiyyy.net/wp-content/uploads/2023/06/Live-Stream.jpg', 'LiveStream', 'LiveStream 2023 ', 'https://phimmoiyyy.net/phim-le/livestream', 'https://helvid.com/play/index/6d0dd7e019b0', '90 phút', '5.9', 'South Korea', 'LiveStream - nói về đang trong giai đoạn chiến tranh lạnh với bạn gái, Dong Joo nhận được một đường link đến buổi phát sóng trực tiếp. Kinh khủng hơn khi cô gái sẽ bị lột trần làm thú vui trên mạng chính là bạn gái anh.', '2024-01-12 10:07:13', '2024-01-12 10:07:13'),
(19, 'https://phimmoiyyy.net/wp-content/uploads/2023/10/Ransomed.jpg', 'Bộ Đôi Báo Thủ', 'Ransomed 2023 ', 'https://phimmoiyyy.net/phim-le/bo-doi-bao-thu-155517', 'https://vie2.opstream7.com/share/4d2e7bd33c475784381a64e43e50922f', '132 phút', '7.9', 'South Korea', 'Bộ Đôi Báo Thủ - tại Beirut năm 1986, trong cuộc nội chiến Lebanon, một nhà ngoại giao Hàn Quốc bị bắt làm con tin không dấu vết. Hai năm trôi qua và bị lãng quên từ lâu, nhà ngoại giao trẻ Min-jun nhận được một cuộc điện thoại chứng minh rằng con tin vẫn còn sống. Với nhiệm vụ được giao, Min-jun được phái đến Beirut để cứu con tin cùng một túi tiền chuộc.', '2024-01-12 10:07:13', '2024-01-12 10:07:13'),
(20, 'https://phimmoiyyy.net/wp-content/uploads/2023/09/The-Nun-2.jpg', 'Ác Quỷ Ma Sơ 2', 'The Nun 2 2023 ', 'https://phimmoiyyy.net/phim-le/ac-quy-ma-so-2775', 'https://hd1080.opstream2.com/share/0180e9a354eccc55463ae4c11abfc5f4', '110 phút', '7.8', 'USA', 'Ác Quỷ Ma Sơ 2 - là bộ phim kinh dị siêu nhiên gothic của Hoa Kỳ được đạo diễn bởi Michael Chaves với kịch bản do Akela Cooper, Ian B. Goldberg và Richard Naing chấp bút dựa trên cốt truyện của Cooper và James Wan. Đây sẽ là phần hậu truyện của Ác quỷ ma sơ và đồng thời là phần phim thứ chín thuộc Vũ trụ The Conjuring.', '2024-01-12 10:07:14', '2024-01-12 10:07:14'),
(21, 'https://phimmoiyyy.net/wp-content/uploads/2023/07/nhiem-vu-bat-kha-thi-7.jpg', 'Nhiệm Vụ: Bất Khả Thi 7 – Nghiệp Báo Phần 1', 'Mission: Impossible 7 - Dead Reckoning Part One 2023 ', 'https://phimmoiyyy.net/phim-le/nhiem-vu-bat-kha-thi-7-nghiep-bao-phan-1-114514', 'https://vie2.opstream7.com/share/ae5e3ce40e0404a45ecacaaf05e5f735', '163 phút', '8.9', 'USA', 'Nhiệm Vụ: Bất Khả Thi 7 – Nghiệp Báo Phần 1 - Ethan Hunt và đội IMF của anh ta bắt đầu nhiệm vụ nguy hiểm nhất của họ: Theo dõi một vũ khí mới đáng sợ đe dọa toàn nhân loại trước khi nó rơi vào tay kẻ xấu.', '2024-01-12 10:07:14', '2024-01-12 10:07:14'),
(22, 'https://phimmoiyyy.net/wp-content/uploads/2023/09/dia-dang-sup-do.jpg', 'Địa Đàng Sụp Đổ', 'Concrete Utopia 2023 ', 'https://phimmoiyyy.net/phim-le/dia-dang-sup-do-154151', 'https://1080.opstream4.com/share/daa178b0fd24b633634b00b9e8781fe2', '130 phút', '7.9', 'South Korea', 'Địa Đàng Sụp Đổ - xoay quanh cơn đại địa chấn san bằng Seoul thành bình địa và hành trình sinh tồn của những người sống sót.', '2024-01-12 10:07:14', '2024-01-12 10:07:14'),
(23, 'https://image.tmdb.org/t/p/w185/y4Q4N7S5CozfSxLecUbGhDj3gz4.jpg', 'Phong Thần 1: Tam Bộ Khúc', 'Creation of the Gods 1: Kingdom Of Storms 2023 ', 'https://phimmoiyyy.net/phim-le/phong-than-1-tam-bo-khuc-541411', 'https://vie2.opstream7.com/share/4da04049a062f5adfe81b67dd755cecc', '140 phút', '8.5', 'China', 'Phong Thần 1: Tam Bộ Khúc - Trụ Vương cấu kết với hồ ly tinh Đát Kỷ khiến người dân lâm vào cảnh lầm than. Khương Tử Nha được lệnh mang Phong Thần Bảng, lợi dụng mọi sự trợ giúp có được để giải thoát bá tính khỏi kiếp nạn, phò Chu diệt Thương.', '2024-01-12 10:07:14', '2024-01-12 10:07:14'),
(24, 'https://phimmoiyyy.net/wp-content/uploads/2023/04/Tieng-goi-am-binh.jpg', 'Tiếng Gọi Âm Binh', 'Khanzab 2023 ', 'https://phimmoiyyy.net/phim-le/tieng-goi-am-binh', 'https://1080.opstream4.com/share/fe1e7a6ec9a1b0684752d2f5c02677dd', '105 phút', '6', 'Indonesia', 'Tiếng Gọi Âm Binh - được dựa trên một vụ thảm sát thầy cúng với tà thuật đen ở Indonesia năm 1998. Chuyện phim diễn ra ở một ngôi làng tại Indonesia và hiện tượng kì lạ ở nhà cầu nguyện trong làng khi các người dân liên tục bị quấy phá khi cầu nguyện.', '2024-01-12 10:07:15', '2024-01-12 10:07:15'),
(25, 'https://phimmoiyyy.net/wp-content/uploads/2023/07/goi-hon-quy-du.jpg', 'Gọi Hồn Quỷ Dữ', 'Talk to Me 2023 ', 'https://phimmoiyyy.net/phim-le/goi-hon-quy-du', 'https://1080.opstream4.com/share/a8b8f6be161a4bdcabcc947b3e72f8b2', '95 phút', '6.1', 'Australia', 'Gọi Hồn Quỷ Dữ - nói về một nhóm bạn phát hiện ra bàn tay ma quái cho phép họ triệu hồi các linh hồn bí ẩn. Họ dần bị cuốn vào trò chơi này mà không biết rằng một trong số họ đã đi quá xa và giải phóng thế lực hắc ám kinh hoàng.', '2024-01-12 10:07:15', '2024-01-12 10:07:15'),
(26, 'https://phimmoiyyy.net/wp-content/uploads/2023/08/Phim-Barbie.jpg', 'Barbie', 'Barbie 2023 ', 'https://phimmoiyyy.net/phim-le/barbie-148741', 'https://vie2.opstream7.com/share/168908dd3227b8358eababa07fcaf091', '114 phút', '6.2', 'United Kingdom', 'Barbie - sẽ được nhào nặn và chắp bút bởi nữ đạo diễn kiêm biên kịch từng nhận nhiều Đề cử Tượng vàng Oscar – Greta Grewig. Hai nhân vật chính Barbie và Ken sẽ được hóa thân bởi nữ diên viên Margot Robbie và nam thần Ryan Gosling, hứa hẹn sẽ tạo nên “chemistry” đáng yêu giữa hai nhân vật búp bê nổi tiếng thế giới.', '2024-01-12 10:07:15', '2024-01-12 10:07:15'),
(27, 'https://phimmoiyyy.net/wp-content/uploads/2023/08/The-Moon.jpg', 'The Moon: Nhiệm Vụ Cuối Cùng', 'The Moon 2023 ', 'https://phimmoiyyy.net/phim-le/the-moon-nhiem-vu-cuoi-cung', 'https://vie2.opstream7.com/share/1e056d2b0ebd5c878c550da6ac5d3724', '129 phút', '9.1', 'South Korea', 'The Moon: Nhiệm Vụ Cuối Cùng - lấy bối cảnh vào năm 2029, khi Hàn Quốc trở thành quốc gia thứ hai đặt chân lên Mặt trăng. Tuy nhiên, một thảm họa không gian đã xảy ra và các phi hành gia bị mắc kẹt trên Mặt trăng. Họ phải tìm cách sống sót và tìm cách quay trở lại Trái đất.', '2024-01-12 10:07:15', '2024-01-12 10:07:15'),
(28, 'https://phimmoiyyy.net/wp-content/uploads/2023/08/Dracula-Quy-Du-Thuc-Tinh.jpg', 'Dracula: Quỷ Dữ Thức Tỉnh', 'The Last Voyage of the Demeter 2023 ', 'https://phimmoiyyy.net/phim-le/dracula-quy-du-thuc-tinh-11154', 'https://vie2.opstream7.com/share/23ce1851341ec1fa9e0c259de10bf87c', '119 phút', '7.4', 'Germany', 'Dracula: Quỷ Dữ Thức Tỉnh - một đoàn thủy thủ tham gia vào nhiệm vụ vận chuyển một “đơn hàng” bí ẩn từ Carpathia đến London trên con tàu Demeter huyền thoại. Hải trình tưởng chừng như đơn giản thế nhưng lại ẩn chứa một cơn ác mộng kinh hoàng khi “đơn hàng” họ vận chuyển đang dần thức tỉnh.', '2024-01-12 10:07:15', '2024-01-12 10:07:15'),
(29, 'https://phimmoiyyy.net/wp-content/uploads/2023/06/Phim-ong-ke.jpg', 'Ông Kẹ', 'The Boogeyman 2023 ', 'https://phimmoiyyy.net/phim-le/phim-ong-ke', 'https://hd1080.opstream2.com/share/0b6412624cfd8ebee546fa2dc1db6945', '99 phút', '6.3', 'USA', 'Ông Kẹ - từ nguyên tác truyện ngắn của bậc thầy truyện kinh dị Stephen King, “Ông Kẹ” kể về câu chuyện của gia đình Harper khi một thực thể siêu nhiên bám theo và liên tục phá rối gia đình họ. Khi mà con quái vật này lấp ló trong bóng tối và chực chờ làm hại cô bé Sawyer như chực chờ một con mồi, chị gái Sadie và bố Will sẽ phải hành động để ngăn chặn điều này trước khi quá muộn.', '2024-01-12 10:07:16', '2024-01-12 10:07:16'),
(30, 'https://phimmoiyyy.net/wp-content/uploads/2023/06/Indiana-Jones-va-Vong-Quay-Dinh-Menh.jpg', 'Indiana Jones 5 và Vòng Quay Định Mệnh', 'Indiana Jones 5 and the Dial of Destiny 2023 ', 'https://phimmoiyyy.net/phim-le/indiana-jones-5-va-vong-quay-dinh-menh', 'https://hd1080.opstream2.com/share/382ffdbd9ec437f2e3dc94d2e137bbf9', '154 phút', '8', 'USA', 'Indiana Jones 5 và Vòng Quay Định Mệnh - sẽ pha trộn giữa thực tế, hư cấu khi lấy bối cảnh năm 1969, lần này Indiana Jones sẽ đối mặt với Đức quốc xã trong thời gian diễn ra cuộc chạy đua vào không gian.', '2024-01-12 10:07:16', '2024-01-12 10:07:16'),
(31, 'https://phimmoiyyy.net/wp-content/uploads/2023/05/meg-2.jpg', 'Cá Mập Siêu Bạo Chúa 2: Vực Sâu', 'Meg 2: The Trench 2023 ', 'https://phimmoiyyy.net/phim-le/ca-map-sieu-bao-chua-2-178152', 'https://vie2.opstream7.com/share/51ef186e18dc00c2d31982567235c559', '150 phút', '7.3', 'China', 'Cá Mập Siêu Bạo Chúa 2: Vực Sâu - tiếp tục nhiệm vụ đã được đề cập trong phần phim trước, nhóm của Jonas Taylor (Jason Statham thủ vai) tiếp cận gần khu vực Rãnh Mariana, nơi họ đụng độ một quái vật bí ẩn, khiến một thành viên trong nhóm thiệt mạng ngay sau đó. Cái chết của người đồng đội báo hiệu cho cả nhóm về một mối đe dọa to lớn đang giấu mình trong những vách đá tối tăm. Thế nhưng, trong cả những viễn cảnh hoang đường nhất, họ cũng không thể ngờ những mối nguy đang chực chờ đe dọa họ có thể tàn ác đến mức nào.', '2024-01-12 10:07:16', '2024-01-12 10:07:16'),
(32, 'https://phimmoiyyy.net/wp-content/uploads/2023/06/Elemental.jpg', 'Xứ Sở Các Nguyên Tố', 'Elemental 2023 ', 'https://phimmoiyyy.net/phim-le/xu-so-cac-nguyen-to-611516', 'https://1080.opstream4.com/share/38320d1a1dd66d409ee7fdf1bf175ae2', '102 phút', '9', 'USA', 'Xứ Sở Các Nguyên Tố - trong một thành phố mà các cư dân lửa, nước, đất, và khí cùng sinh sống. Một cô gái nóng nảy và một chàng trai gió-chiều-nào-xoay-chiều-ấy sẽ khám phá ra rằng họ có nhiều điểm giống nhau.', '2024-01-12 10:07:16', '2024-01-12 10:07:16'),
(33, 'https://phimmoiyyy.net/wp-content/uploads/2023/07/Bao-trang-3.jpg', 'Bão Trắng 3', 'The White Storm 3: Heaven or Hell 2023 ', 'https://phimmoiyyy.net/phim-le/bao-trang-3', 'https://vie2.opstream7.com/share/912d2b1c7b2826caf99687388d2e8f7c', '125 phút', '8.3', 'China', 'Bão Trắng 3 - Âu Chí Viễn và Trương Kiến Hành là cảnh sát chìm hoạt động trong một băng đảng ma túy do ông trùm Khang Tố Sai đứng đầu tại Hongkong. Trong một lần bị thương nặng, Trương Kiến Hành đã được cứu bởi ông trùm ma túy trên một con tàu vượt biên tới khu Tam Giác Vàng.', '2024-01-12 10:07:17', '2024-01-12 10:07:17'),
(34, 'https://phimmoiyyy.net/wp-content/uploads/2023/07/phim-quy-cong-tu.jpg', 'Quý Công Tử', 'The Childe 2023 ', 'https://phimmoiyyy.net/phim-le/quy-cong-tu-154121', 'https://1080.opstream4.com/share/ecf0f13c45ae9b1787dd211283ee4cb0', '118 phút', '9.5', 'South Korea', 'Quý Công Tử - xoay quanh người đàn ông bí ẩn được biết đến với tên gọi “Quý Công Tử”. Gã đột nhiên xuất hiện trước mắt Marco, một thanh niên người Philippines mơ ước trở thành vận động viên boxing chuyên nghiệp, lang thang khắp các sàn đấu bất hợp pháp tại đây.', '2024-01-12 10:07:17', '2024-01-12 10:07:17'),
(35, 'https://phimmoiyyy.net/wp-content/uploads/2023/04/ta-chu-cam.jpg', 'Tà Chú Cấm', 'Home for Rent 2023 ', 'https://phimmoiyyy.net/phim-le/ta-chu-cam', 'https://1080.opstream4.com/share/1c4196d0ff7fe4e94bdca98fb251bc25', '124 phút', '6.9', 'Thailand', 'Tà Chú Cấm - khám phá nỗi ám ảnh từ sự kiện thật tại Thái Lan, kể về gia đình Ning và Kwin khi họ chuyển đến căn hộ chung cư giá rẻ. Hành vi kỳ lạ của Kwin và hình xăm bí ẩn dẫn đến nguy hiểm đối với con gái Ing. Bí mật đen tối trong ngôi nhà thuê đang chờ đợi.', '2024-01-12 10:07:17', '2024-01-12 10:07:17'),
(36, 'https://phimmoiyyy.net/wp-content/uploads/2023/06/Nguoi-Nhen-Du-Hanh-Vu-Tru-Nhen-2023.jpg', 'Người Nhện: Du Hành Vũ Trụ Nhện', 'Spider-Man: Across the Spider-Verse 2023 ', 'https://phimmoiyyy.net/phim-le/nguoi-nhen-du-hanh-vu-tru-nhen-15411', 'https://1080.opstream4.com/share/425e4714036e70b93d1683fc33b757e6', '140 phút', '9.3', 'USA', 'Người Nhện: Du Hành Vũ Trụ Nhện - Miles Morales tái xuất trong phần tiếp theo của bom tấn hoạt hình từng đoạt giải Oscar – Spider-Man: Across the Spider-Verse. Sau khi gặp lại Gwen Stacy, chàng Spider-Man thân thiện đến từ Brooklyn phải du hành qua đa vũ trụ và gặp một nhóm Người Nhện chịu trách nhiệm bảo vệ các thế giới song song.', '2024-01-12 10:07:18', '2024-01-12 10:07:18'),
(37, 'https://phimmoiyyy.net/wp-content/uploads/2023/07/quy-quyet-cua-do-vo-dinh.jpg', 'Quỷ Quyệt 5: Cửa Đỏ Vô Định', 'Insidious 5: The Red Door 2023 ', 'https://phimmoiyyy.net/phim-le/quy-quyet-5-cua-do-vo-dinh-151410', 'https://hd1080.opstream2.com/share/e75d0b169ffeb90d4b805790ce68a239', '110 phút', '7.2', 'USA', 'Quỷ Quyệt 5: Cửa Đỏ Vô Định - lấy bối cảnh sau mười năm kể từ hai phần phim đầu tiên, Josh Lambert (Patrick Wilson) đang chuyển đến sinh sống tại phía Đông để đưa cậu con trai Dalton (Ty Simpkins) đến học tại một trường đại học bình dị, phủ đầy cây thường xuân. Tuy nhiên, giấc mơ đại học của Dalton lại trở lại một ác mộng khi những con quỷ bị kìm hãm trong quá khứ bằng một cách nào đó đã quay trở lại và tiếp tục ám hai cha con.', '2024-01-12 10:07:18', '2024-01-12 10:07:18'),
(38, 'https://phimmoiyyy.net/wp-content/uploads/2023/07/phim-vo-danh.jpg', 'Vô Danh', 'Hidden Blade 2023 ', 'https://phimmoiyyy.net/phim-le/vo-danh', 'https://1080.opstream4.com/share/95f2351eb791917e3e1cee823cf25969', '128 phút', '9.2', 'China', 'Vô Danh - câu chuyện bắt đầu với sự kiện ngày 7 tháng 12 năm 1941, khi mà Nhật Bản tấn công Trân Châu Cảng. Ngày hôm sau, chính phủ bù nhìn Vương theo chân Nhật Bản tuyên chiến với Anh và Mỹ, và Nhật Bản chiếm đóng toàn bộ lãnh thổ Thượng Hải một cách tượng trưng. Với sự bùng nổ của chiến tranh Thái Bình Dương, tình hình kháng chiến của Trung Quốc đã hoàn toàn thay đổi. Bộ phận đặc biệt của ĐCSTQ dưới sự lãnh đạo của đảng cộng sản Trung Quốc hoạt động ở Trùng Khánh, mở ra những sự kiện mới.', '2024-01-12 10:07:18', '2024-01-12 10:07:18'),
(39, 'https://phimmoiyyy.net/wp-content/uploads/2023/06/Phim-Transformers-7.jpg', 'Transformers 7: Quái Thú Trỗi Dậy', 'Transformers: Rise of the Beasts 2023 ', 'https://phimmoiyyy.net/phim-le/transformers-quai-thu-troi-day', 'https://1080.opstream4.com/share/418f995ca45e4394daa4447bd637ec4d', '110 phút', '8.5', 'USA', 'Transformers 7: Quái Thú Trỗi Dậy - dựa trên sự kiện Beast Wars trong loạt phim hoạt hình “Transformers”, nơi mà các robot có khả năng biến thành động vật khổng lồ cùng chiến đấu chống lại một mối đe dọa tiềm tàng.', '2024-01-12 10:07:18', '2024-01-12 10:07:18'),
(40, 'https://phimmoiyyy.net/wp-content/uploads/2023/06/Ruby-Thuy-Quai-Tuoi-Teen.jpg', 'Ruby Thủy Quái Tuổi Teen', 'Ruby Gillman, Teenage Kraken 2023 ', 'https://phimmoiyyy.net/phim-le/ruby-thuy-quai-tuoi-teen', 'https://1080.opstream4.com/share/bcc13206fd8338d229f0ac74adab7f26', '91 phút', '9.1', 'USA', 'Ruby Thủy Quái Tuổi Teen - kể về cô bé Ruby Gillman nhút nhát, tình cờ phát hiện ra mình là một phần của dòng dõi thủy quái hoàng gia huyền thoại. Và kể từ giây phút này, số phận của cô bé dưới đáy đại dương bỗng thay thổi hoàn toàn, cùng với đó là một sứ mệnh lớn hơn tất cả những gì cô từng mơ đến.', '2024-01-12 10:07:18', '2024-01-12 10:07:18'),
(41, 'https://phimmoiyyy.net/wp-content/uploads/2022/06/Nha-ga-nuot-chung.jpg', 'Nhà Ga Nuốt Chửng', 'Kisaragi Station 2022 ', 'https://phimmoiyyy.net/phim-le/nha-ga-nuot-chung', 'https://vk.com/video_ext.php?oid=790453278&id=456239917&hash=6dca40258eeba7f0&hd=2&autoplay=1', '82 phút', '6.5', 'Japan', 'Nhà Ga Nuốt Chửng - Haruna Tsunematsu (Yuri Tsunematsu) đang học nghiên cứu về chuyên ngành văn hoá dân gian tại trường đại học. Cô quyết định lựa chọn truyền thuyết đô thị nổi tiếng là “Nhà ga Kisagari” để làm luận văn tốt nghiệp. Đây là câu chuyện rùng rợn từng được lưu truyền và khuấy đảo cộng động mạng vào năm 2004.', '2024-01-12 10:07:19', '2024-01-12 10:07:19'),
(42, 'https://phimmoiyyy.net/wp-content/uploads/2023/06/Sieu-nhan-cuong-phong-ky-niem-20-nam.jpg', 'Siêu Nhân Cuồng Phong Kỷ Niệm 20 Năm', 'Ninpu Sentai Hurricaneger: 20 Years After 2023 ', 'https://phimmoiyyy.net/phim-le/sieu-nhan-cuong-phong-ky-niem-20-nam', 'https://vk.com/video_ext.php?oid=754118842&id=456241611&hash=2e537296c9123e7f&autoplay=1', '110 phút', '8.8', 'Japan', 'Siêu Nhân Cuồng Phong Kỷ Niệm 20 Năm -undefined', '2024-01-12 10:07:19', '2024-01-12 10:07:19'),
(43, 'https://phimmoiyyy.net/wp-content/uploads/2023/07/ve-binh-dai-ngan-ha.jpg', 'Vệ Binh Dải Ngân Hà 3', 'Guardians of the Galaxy Volume 3 2023 ', 'https://phimmoiyyy.net/phim-le/ve-binh-dai-ngan-ha-4-745141', 'https://hd1080.opstream2.com/share/82aa20a74dbe3e0e85c23ba8d645d3ce', '150 phút', '9.3', 'USA', 'Vệ Binh Dải Ngân Hà 3 - sau khi mua Knowhere từ The Collector, đội Vệ binh dải Ngân Hà cố gắng biến nó thành nơi trú ẩn an toàn cho những người tị nạn sau cú búng tay di dời. Nhưng sau một cuộc tấn công tàn bạo, Peter Quill, vẫn cảm xúc vì mất Gamora, phải tập hợp các Vệ binh để thực hiện sứ mệnh bảo vệ vũ trụ và bảo vệ một người trong số họ khỏi kẻ thù chung nguy hiểm.', '2024-01-12 10:07:19', '2024-01-12 10:07:19'),
(44, 'https://phimmoiyyy.net/wp-content/uploads/2023/05/Vay-Ham-Khong-Loi-Thoat.jpg', 'Vây Hãm: Không Lối Thoát', 'The Roundup: No Way Out 2023 ', 'https://phimmoiyyy.net/phim-le/vay-ham-khong-loi-thoat-717111', 'https://1080.opstream4.com/share/a330238f5e5026982abe38b8f2215898', '105 phút', '9.4', 'Korean', 'Vây Hãm: Không Lối Thoát - quái vật cơ bắp Seok-do (Ma Dong Seok) dẫn đầu đội hình sự truy lùng đường dây buôn chất cấm của thiếu gia Joo Seong Cheol. Cuộc truy đuổi càng thêm gay cấn khi cú đấm công lý “chú Ma” chạm trán thanh kiếm lừng lẫy chốn giang hồ Nhật Bản.', '2024-01-12 10:07:20', '2024-01-12 10:07:20'),
(45, 'https://phimmoiyyy.net/wp-content/uploads/2023/07/Gangnam-That-Thu.jpg', 'Gangnam Thất Thủ', 'Gangnam Zombie 2023 ', 'https://phimmoiyyy.net/phim-le/gangnam-that-thu', 'https://short.ink/1BCcX6blH', '81 phút', '3.1', 'South Korea', 'Gangnam Thất Thủ - khi bầy xác sống điên cuồng tấn công khu phố Gangnam của Hàn Quốc. Mắc kẹt với bầy xác sống trong toà nhà ở khu phố Gangnam nổi tiếng, chuyện gì sẽ xảy ra? Hành trình sống sót đầy kịch tính trong Gangnam Thất Thủ – bộ phim mang lại làn gió mới trong dòng phim xác sống', '2024-01-12 10:07:20', '2024-01-12 10:07:20'),
(46, 'https://phimmoiyyy.net/wp-content/uploads/2023/06/The-Flash-vietsub.jpg', 'The Flash', 'The Flash 2023 ', 'https://phimmoiyyy.net/phim-le/phim-the-flash-1', 'https://vk.com/video_ext.php?oid=754118842&id=456241768&hash=432ce61b3c4f5c19&autoplay=1', '144 phút', '7.2', 'USA', 'The Flash - vì muốn cứu mẹ, Barry Allen tìm cách quay trở lại quá khứ tuy nhiên hành động của anh vô tình thiết lập một thế giới mới nơi thế lực ngoài hành tinh của tướng Zod quay trở lại và tìm cách tàn phá Trái Đất một lần nữa', '2024-01-12 10:07:20', '2024-01-12 10:07:20'),
(47, 'https://phimmoiyyy.net/wp-content/uploads/2023/03/the-little-mermaid.jpg', 'Nàng Tiên Cá', 'The Little Mermaid 2023 ', 'https://phimmoiyyy.net/phim-le/the-little-mermaid-2', 'https://hd1080.opstream2.com/share/ea2348dbd72165cc69aa7c26e89ed8d9', '110 phút', '5.4', 'USA', 'Nàng Tiên Cá - là câu chuyện được yêu thích về Ariel – một nàng tiên cá trẻ xinh đẹp và mạnh mẽ với khát khao phiêu lưu. Ariel là con gái út của Vua Triton và cũng là người ngang ngạnh nhất, nàng khao khát khám phá về thế giới bên kia đại dương.', '2024-01-12 10:07:20', '2024-01-12 10:07:20'),
(48, 'https://phimmoiyyy.net/wp-content/uploads/2023/06/Phim-quai-vat-den.jpg', 'Quái Vật Đen', 'The Black Demon 2023 ', 'https://phimmoiyyy.net/phim-le/phim-quai-vat-den', 'https://1080.opstream4.com/share/245e57794c369c8617378e285ee9755c', '100 phút', '6.1', 'Dominican Republic', 'Quái Vật Đen - xoay quanh câu chuyện khi kỳ nghỉ bình dị của gia đình Oilman Paul Sturges biến thành cơn ác mộng. Bởi họ đã gặp phải một con cá mập Megalodon hung dữ, không từ bất kỳ khoảnh khắc nào để bảo vệ lãnh thổ của mình. Bị mắc kẹt và tấn công liên tục, Paul và gia đình của mình phải tìm cách để an toàn sống sót trở về bờ trước khi con cá mập khát máu này tấn công lần nữa.', '2024-01-12 10:07:20', '2024-01-12 10:07:20'),
(49, 'https://phimmoiyyy.net/wp-content/uploads/2023/04/avatar-2.jpg', 'Avatar: Dòng Chảy Của Nước', 'Avatar: The Way of Water 2022 ', 'https://phimmoiyyy.net/phim-le/phim-avatar-dong-chay-cua-nuoc', 'https://kd.hd-bophim.com/share/86ff09548a5c6eff1ec764a28b6c8112', '192 phút', '8.6', 'USA', 'Avatar: Dòng Chảy Của Nước - lấy bối cảnh 10 năm sau những sự kiện xảy ra ở phần đầu tiên. Phim kể câu chuyện về gia đình mới của Jake Sully (Sam Worthington thủ vai) cùng những rắc rối theo sau và bi kịch họ phải chịu đựng khi phe loài người xâm lược hành tinh Pandora.', '2024-01-12 10:07:21', '2024-01-12 10:07:21'),
(50, 'https://phimmoiyyy.net/wp-content/uploads/2023/06/phim-quai-vat-song-me-kong.jpg', 'Quái Vật Sông Mekong', 'The Lake 2022 ', 'https://phimmoiyyy.net/phim-le/quai-vat-song-mekong', 'https://vk.com/video_ext.php?oid=790453278&id=456239658&hash=2530477bff90ce33&autoplay=1', '105 phút', '4.1', 'Thailand', 'Quái Vật Sông Mekong - đặt bối cảnh tại làng Buengkan, một tỉnh thuộc miền Bắc Thái Lan, Quái Vật Sông Mekong chính thức trở thành cơn ác mộng đối với dân làng và được coi là khơi nguồn của thảm họa ngay khi nó phá hủy mọi thứ và khiến mọi người bị mất kết nối hoàn toàn với thế giới bên ngoài. Sự kiện chấn động toàn Thái Lan này đã khiến các cơ quan chức năng cùng những nhà khoa học vô tình đến Bueng Kan để tiến hành nghiên cứu phải huy động tất cả các lực lượng để bắt con quái vật điên rồ này trước khi quá muộn.', '2024-01-12 10:07:21', '2024-01-12 10:07:21'),
(51, 'https://phimmoiyyy.net/wp-content/uploads/2023/04/Tram-tau-ma.jpg', 'Trạm Tàu Ma', 'The Ghost Station 2023 ', 'https://phimmoiyyy.net/phim-le/tram-tau-ma', 'https://vie2.opstream7.com/share/1afa34a7f984eeabdbb0a7d494132ee5', '80 phút', '5.4', 'South Korea', 'Trạm Tàu Ma - lời đồn ma ám về nhà ga Oksu ngày càng nhiều khi những vụ án kinh hoàng liên tục xảy ra. Một đường ray cũ kỹ, một chiếc giếng bỏ hoang, những con số gây ám ảnh hay những vết thương kỳ dị trên thi thể người xấu số… Tất cả dẫn đến một bí mật đau lòng bị chôn vùi nhiều năm trước.', '2024-01-12 10:07:21', '2024-01-12 10:07:21'),
(52, 'https://phimmoiyyy.net/wp-content/uploads/2023/05/oan-hon.jpg', 'Oán Hồn', 'The Creeping 2022 ', 'https://phimmoiyyy.net/phim-le/oan-hon', 'https://www.youtube.com/embed/Et7DujVC7Kk ', '93 phút', '7.8', 'United Kingdom', 'Oán Hồn - trải nghiệm thời thơ ấu đau thương, Anna trở về căn nhà xưa để chăm sóc người bà ốm yếu. Từ đó, những điều kỳ lạ bắt đầu xảy ra và các sự kiện kỳ quái dần xuất hiện cho đến khi Anna phát hiện ra mọi việc có liên quan đến một quá khứ bi thảm đã ám lên các thành viên trong gia đình. Điều gì sẽ xảy ra với Anna khi mọi oán niệm được ẩn giấu phía sau ngôi nhà và người bà kỳ lạ?', '2024-01-12 10:07:21', '2024-01-12 10:07:21'),
(53, 'https://phimmoiyyy.net/wp-content/uploads/2023/05/nhung-ke-thao-tung.jpg', 'Những Kẻ Thao Túng', 'Hypnotic 2023 ', 'https://phimmoiyyy.net/phim-le/nhung-ke-thao-tung', 'https://1080.opstream4.com/share/27f6f78fe227d299b12bd3031861913e', '94 phút', '8.9', 'United Kingdom', 'Những Kẻ Thao Túng - theo chân thám tử Daniel Rourke (Ben Affleck) trong hành trình tìm kiếm cô con gái bị mất tích. Anh sớm nhận ra tất cả những chuyện bí ẩn này đều liên quan đến một người đàn ông có sức mạnh thôi miên, bẻ cong thực tại. Với sự hỗ trợ từ nhà ngoại cảm Diana Cruz (Alice Braga), Daniel bắt đầu truy đuổi hắn và phải tìm mọi cách để đưa con gái mình trở về nhà an toàn.', '2024-01-12 10:07:22', '2024-01-12 10:07:22'),
(54, 'https://phimmoiyyy.net/wp-content/uploads/2023/04/Phim-man-giang-hong.jpg', 'Mãn Giang Hồng', 'Full River Red 2023 ', 'https://phimmoiyyy.net/phim-le/man-giang-hong', 'https://1080.opstream4.com/share/2ef8781369f660c4e873bdf5fbf863d4', '157 phút', '8.8', 'China', 'Mãn Giang Hồng - lấy bối cảnh thời Thiệu Hưng, nhà Nam Tống (Trung Quốc), bốn năm sau khi Tướng quân Nhạc Phi qua đời. Vào đêm trước cuộc đàm phán dẫn đầu bởi Tể tướng Tần Cối với nhà Tấn, sứ giả của nước này bị phát hiện chết tại dinh thự của ông ta, và bức thư bí mật mang theo đã biến mất. Người lính Trương Đại và phó chỉ huy quân đội Tôn Quân tình cờ bị cuốn vào âm mưu to lớn này và được lệnh cho cả hai phải tìm ra kẻ giết người trong vòng một giờ.', '2024-01-12 10:07:22', '2024-01-12 10:07:22'),
(55, 'https://phimmoiyyy.net/wp-content/uploads/2023/03/John-Wick-4.jpg', 'John Wick: Phần 4', 'John Wick: Chapter 4 2023 ', 'https://phimmoiyyy.net/phim-le/phim-john-wick-phan-4-15472', 'https://hd1080.opstream2.com/share/8c3b5e395a73855eb15bf81d2f249f00', '169 phút', '8.6', 'USA', 'John Wick: Phần 4 - là câu chuyện của John Wick (Keanu Reeves) đã khám phá ra con đường để đánh bại High Table. Nhưng trước khi có thể kiếm được tự do, Wick phải đối đầu với kẻ thù mới với những liên minh hùng mạnh trên toàn cầu và những lực lượng biến những người bạn cũ thành kẻ thù.', '2024-01-12 10:07:22', '2024-01-12 10:07:22'),
(56, 'https://phimmoiyyy.net/wp-content/uploads/2023/05/Phim-Sisu-Gia-Gan-Bao-Thu.jpg', 'Sisu: Già Gân Báo Thù', 'Sisu 2023 ', 'https://phimmoiyyy.net/phim-le/sisu-gia-gan-bao-thu', 'https://hd1080.opstream2.com/share/0a9a820b2c608bb2c204ad980d7c60c8', '91 phút', '9.3', 'Finland', 'Sisu: Già Gân Báo Thù - khi một cựu quân nhân phát hiện ra vàng ở vùng hoang dã Lapland cố gắng mang chiến lợi phẩm vào thành phố, những người lính Đức Quốc xã do một sĩ quan SS tàn bạo chỉ huy đã chiến đấu với anh ta.', '2024-01-12 10:07:22', '2024-01-12 10:07:22'),
